angular.module("myModule1",["ng"]).controller("myCtrl1",function(a){a.cart=[];a.cart.push({price:10,count:1});a.addProduct=function(){var b=new Object();b.price=Math.floor(Math.random()*100);b.count=1;a.cart.push(b)};a.total=function(){console.log("重新计算总价");var c=0;for(var b=0;b<a.cart.length;b++){var d=a.cart[b];c+=d.price*d.count}return c}});